// Alerts لحظه‌ای و ثبت Downtime
const status = $json["status"];
const device = $json["device"];
const sensor = $json["sensor"];
const reason = $json["message"] || 'نامشخص';
const now = new Date();

let text = '';
let emoji = '';
let downData = $workflow.getGlobal('prtg_down') || {};
let history = $workflow.getGlobal('prtg_down_history') || {};

const key = device + '|' + sensor;

if(status === 'Down') {
  downData[key] = { start: now, reason: reason };
  $workflow.setGlobal('prtg_down', downData);
  emoji = '⚠️';
  text = `${emoji} *PRTG Alert*: Sensor DOWN!\n*Device:* ${device}\n*Sensor:* ${sensor}\n*Reason:* ${reason}\n*Time:* ${now.toLocaleString()}`;
} else if(status === 'Up') {
  if(downData[key]) {
    const start = new Date(downData[key].start);
    const diffMins = (now - start) / 60000;
    const downtime = diffMins.toFixed(2);
    const downReason = downData[key].reason;

    // تعیین Emoji بر اساس زمان Downtime
    if(diffMins < 1) emoji = '✅';
    else if(diffMins < 3) emoji = '⚠️';
    else if(diffMins >= 5) emoji = '🔴';
    else emoji = '⚠️';

    // ثبت در history برای گزارش روزانه
    if(!history[key]) history[key] = { device: device, sensor: sensor, totalDowntime: 0, count: 0, visual: '' };
    history[key].totalDowntime += diffMins;
    history[key].count += 1;
    history[key].visual += emoji;
    $workflow.setGlobal('prtg_down_history', history);

    delete downData[key];
    $workflow.setGlobal('prtg_down', downData);

    text = `${emoji} *PRTG Notification*: Sensor UP\n*Device:* ${device}\n*Sensor:* ${sensor}\n*Downtime:* ${downtime} دقیقه\n*Reason:* ${downReason}\n*Time:* ${now.toLocaleString()}`;
  } else {
    text = `ℹ️ *PRTG Notification*: Sensor UP but no previous DOWN record.\n*Device:* ${device}\n*Sensor:* ${sensor}\n*Time:* ${now.toLocaleString()}`;
  }
}
$json["telegram_text"] = text;
return $json;

// آماده‌سازی گزارش روزانه بصری
const history = $workflow.getGlobal('prtg_down_history') || {};
let reportLines = [];
for (const key in history) {
  const entry = history[key];
  reportLines.push(`*${entry.device} - ${entry.sensor}*: ${entry.visual}  ${entry.count} قطعی، مجموع Downtime: ${entry.totalDowntime.toFixed(2)} دقیقه`);
}
const reportText = reportLines.length > 0 ? `📊 *گزارش روزانه PRTG*\n\n${reportLines.join('\n')}` : '📊 هیچ Downtime ثبت نشده است.';
$json["telegram_text"] = reportText;
return $json;
